import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";

const monday = mondaySdk();
var completed = 0;

class App extends React.Component {
    constructor(props) {
        super(props);

        // Default state
        this.state = {
            settings: {},
            name: "",
        };
    }

    componentDidMount() {
        monday.storage.instance.getItem('completed').then(res => {
            completed = res.data.value;
         });
         if (completed == null) {
            monday.execute("notice", {
                message: "Could not find amount of completed!",
                type: "error",
                timeout: 1000,
            });
             completed = 0;
         }

        monday.listen("events", (res) => {
            completed++;
            monday.execute("notice", {
                message: "Completed: " + completed,
                type: "info",
                timeout: 1000,
            });
            document.getElementById("title").innerHTML = completed;

            monday.storage.instance.setItem("completed", completed);
        },
        { type: "change_column_value" }
        );
    }

    render() {
        return <div className="App" id="title">Rocket ready to launch when you are!</div>;
    }
}

export default App;
