import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";

const monday = mondaySdk();
var completed = 0;

class App extends React.Component {
    constructor(props) {
        super(props);

        // Default state
        this.state = {
            settings: {},
            name: "",
        };
    }

    componentDidMount() {
        monday.storage.instance.getItem('completed').then(res => {
            completed = res.data.value;
         });
         if (completed == null) { completed = 0; }

         monday.listen("context", res => {
            this.setState({context: res.data});

            monday.api(`query ($boardIds: [Int]) { boards (ids:$boardIds) { name items(limit:1) { name column_values } } }`,
              { variables: {boardIds: this.state.context.boardIds} }
            )
            .then(res => {
              this.setState({boardData: res.data});
              var i = 0;
              for (i in this.state.boardData.boards) {
                var j = 0;
                for (j in this.state.boardData.boards[i].items) {
                    monday.execute("notice", {
                        message: JSON.stringify(this.state.boardData.boards[i], null, 2),
                        type: "info",
                        timeout: 1000,
                    });
                }
              }
            });
          })

        monday.listen("events", (res) => {
            completed++;
            monday.execute("notice", {
                message: "Completed: " + completed,
                type: "info",
                timeout: 1000,
            });
            document.getElementById("title").innerHTML = completed;

            monday.storage.instance.setItem("completed", completed);
        },
        { type: "change_column_value" }
        );
    }

    render() {
        return <div className="App" id="title">
            {JSON.stringify(this.state.boardData, null, 2)} 
        </div>;
    }
}

export default App;
