import React, { createElement } from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import Matter from "matter-js";

const monday = mondaySdk();
var completed = [];
var completed_state = "Done";

// module aliases
var Engine = Matter.Engine,
Render = Matter.Render,
World = Matter.World,
Bodies = Matter.Bodies,
Body = Matter.Body,
Events = Matter.Events,
MouseConstraint = Matter.MouseConstraint,
Mouse = Matter.Mouse;

var engine,
render;

var crates = [];

class App extends React.Component {
    constructor(props) {
        super(props);

        // Default state
        this.state = {
            settings: {
                completed_state: "Done"
            },
            name: "",
        };
    }

    componentDidMount() {

        engine = Engine.create();

        // create a renderer
        render = Render.create({
            element: this.refs.launch_site,
            engine: engine,
            options: {
                wireframes: false,
                background: '#fff',
                width: window.innerWidth,
                height: window.innerHeight - 100
            }
        });
        render.options.wireframes = false;

        var ground = Bodies.rectangle(window.innerWidth / 2 - 250, window.innerHeight - 100, window.innerWidth + 500, 20, { 
            isStatic: true,
            render: { fillStyle: '#fff' }
        });

        var mouse = Mouse.create(render.canvas),
        mouseConstraint = MouseConstraint.create(engine, {
            mouse: mouse,
            constraint: {
                stiffness: 0.2,
                render: {
                    visible: false
                }
            }
        });

        World.add(engine.world, [ground, mouseConstraint]);
        Engine.run(engine);
        Render.run(render);

        Events.on(engine, 'tick', function(event) {
            var mousePosition = mouse.position;
            var cratesUnderMouse = Matter.Query.point(crates, {x: mousePosition.x, y: mousePosition.y});

            const label = document.getElementById("hover_label");
            if (cratesUnderMouse.length > 0) {
                let crate = cratesUnderMouse[0];
                let newXPos = crate.position.x - (crate.width / 2);
                label.innerHTML = crate.id;
                label.style.top = crate.position.y + "px";
                label.style.left = newXPos + "px";
                label.style.opacity = 1;
            } else {
                label.style.opacity = 0;
            }
        });

        //Load boards listener
         monday.listen("context", res => {
            this.setState({context: res.data});

            monday.api(`query ($boardIds: [Int]) { boards (ids:$boardIds) { name items { name column_values { title text } } } }`,
              { variables: {boardIds: this.state.context.boardIds} }
            )

            .then(res => {
                this.setState({boardData: res.data});
                this.loadCompleted();
            });
          })

        // Settings changed listener
        monday.listen("settings", res => {
            this.setState({ settings: res.data });
            completed_state = this.state.settings.completed_state;

             this.loadCompleted();
        });
    }

    render() {
        return <div className="App" ref="launch_site">
            <p id="hover_label">Hover</p>
            <div className="launchDiv">
                <p className="launchTitle" id="title"></p>
                <button onClick={moveCrates} className="launchButton">Launch now</button>
            </div>
        </div>;
    }

    /** Loads completed items into the completed array */
    loadCompleted() {

        completed = [];
        for (let i in this.state.boardData.boards) {
            for (let j in this.state.boardData.boards[i].items) {

                var itemName = this.state.boardData.boards[i].items[j].name;

                for (let k in this.state.boardData.boards[i].items[j].column_values) {
                    if (this.state.boardData.boards[i].items[j].column_values[k].text === completed_state) {
                        completed[completed.length] = itemName;
                        break;
                    }
                }   
            }
        }
        document.getElementById("title").innerHTML = completed.length + " items ready to launch!";
        this.createBoxes();
    }

    /** Creates crates based on data in the completed array */
    createBoxes() {
        for (let i in crates) {
            World.remove(engine.world, crates[i]);
        }
        crates = [];
        for (let i in completed) {

            let x = getRandomInt(200, 800);
            let y = getRandomInt(50, 400);
            let size = getRandomInt(50, 80);

            let spriteNumber = getRandomInt(1, 8);

            let crate = Bodies.rectangle(x, y, size, size, {
                render: {
                    sprite: {
                        texture: "/sprites/crates/crate_" + spriteNumber + ".png",
                        xScale: size / 480,
                        yScale: size / 480
                    }
                },
                id: completed[i],
            });

            World.add(engine.world, [crate]);
            crates[crates.length] = crate;

        }
    }
}

function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

function moveCrates() {
    for (let i in crates) {
        let crate = crates[i];
        Body.applyForce(crate, {x: - 100, y: window.innerHeight + 100}, {x: 0.3, y: -0.35});
    }
}

export default App;
