import React, { createElement } from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import Matter from "matter-js";

const monday = mondaySdk();
var completed = [];
var completed_state = "Done";


// module aliases
var Engine = Matter.Engine,
Render = Matter.Render,
World = Matter.World,
Bodies = Matter.Bodies;

var engine;
var render;


class App extends React.Component {
    constructor(props) {
        super(props);

        // Default state
        this.state = {
            settings: {
                completed_state: "Done"
            },
            name: "",
        };
    }

    componentDidMount() {

        // create an engine
        engine = Engine.create();

        // create a renderer
        render = Render.create({
            element: this.refs.launch_site,
            engine: engine
        });

        // create two boxes and a ground
        var ground = Bodies.rectangle(400, 610, 810, 60, { isStatic: true });

        // add all of the bodies to the world
        World.add(engine.world, [ground]);

        // run the engine
        Engine.run(engine);

        // run the renderer
        Render.run(render);


        //Load boards listener
         monday.listen("context", res => {
            this.setState({context: res.data});

            monday.api(`query ($boardIds: [Int]) { boards (ids:$boardIds) { name items { name column_values { title text } } } }`,
              { variables: {boardIds: this.state.context.boardIds} }
            )

            .then(res => {
                this.setState({boardData: res.data});
                this.loadCompleted();
            });
          })

        // Settings changed listener
        monday.listen("settings", res => {
            this.setState({ settings: res.data });
            completed_state = this.state.settings.completed_state;

             this.loadCompleted();
        });
    }

    render() {
        return <div className="App" ref="launch_site"></div>;
    }

    /** Loads completed items into the completed array */
    loadCompleted() {
        completed = [];
        for (let i in this.state.boardData.boards) {
            for (let j in this.state.boardData.boards[i].items) {

                var itemName = this.state.boardData.boards[i].items[j].name;

                for (let k in this.state.boardData.boards[i].items[j].column_values) {
                    if (this.state.boardData.boards[i].items[j].column_values[k].text == completed_state) {

                        completed[completed.length] = itemName;
                    }
                }   
            }
        }
        this.createBoxes();
    }

    /** Creates crates based on data in the completed array */
    createBoxes() {
        for (var i in completed) {

            var boxA = Bodies.rectangle(400, 200, 80, 80);
            World.add(engine.world, [boxA]);
        }
    }
}

export default App;
