import React, { createElement } from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import Matter from "matter-js";

const monday = mondaySdk();
var completed = [];
var completed_state = "Done";


// module aliases
var Engine = Matter.Engine,
Render = Matter.Render,
World = Matter.World,
Bodies = Matter.Bodies,
Body = Matter.Body,
Events = Matter.Events,
MouseConstraint = Matter.MouseConstraint,
Mouse = Matter.Mouse;

var engine;
var render;

var crates = [];

class App extends React.Component {
    constructor(props) {
        super(props);

        // Default state
        this.state = {
            settings: {
                completed_state: "Done"
            },
            name: "",
        };
    }

    componentDidMount() {

        engine = Engine.create();

        // create a renderer
        render = Render.create({
            element: this.refs.launch_site,
            engine: engine,
            options: {
                hasBounds: true,
                wireframes: false,
                background: '#fff',
                showIds: true,
                width: window.innerWidth,
                height: window.innerHeight - 100
            }
        });
        var ground = Bodies.rectangle(window.innerWidth / 2, window.innerHeight - 100, window.innerWidth, 20, { 
            isStatic: true,
            render: { fillStyle: '#fff' }
        });

        var mouse = Mouse.create(render.canvas),
        mouseConstraint = MouseConstraint.create(engine, {
            mouse: mouse,
            constraint: {
                stiffness: 0.2,
                render: {
                    visible: false
                }
            }
        });

        World.add(engine.world, [ground, mouseConstraint]);
        Engine.run(engine);
        Render.run(render);


        // an example of using mouse events on a mouse
        Events.on(mouseConstraint, 'mousedown', function(event) {
            var mousePosition = event.mouse.position;
        });

        //Load boards listener
         monday.listen("context", res => {
            this.setState({context: res.data});

            monday.api(`query ($boardIds: [Int]) { boards (ids:$boardIds) { name items { name column_values { title text } } } }`,
              { variables: {boardIds: this.state.context.boardIds} }
            )

            .then(res => {
                this.setState({boardData: res.data});
                this.loadCompleted();
            });
          })

        // Settings changed listener
        monday.listen("settings", res => {
            this.setState({ settings: res.data });
            completed_state = this.state.settings.completed_state;

             this.loadCompleted();
        });
    }

    moveCrates() {
        alert("Moving construction vehicle!")
        for (let i in crates) {
            let crate = crates[i];
            Body.setPosition(crate, {x: 800, y: 100})
        }
    }

    render() {
        return <button onClick={moveCrates()} className="launchButton">Launch now</button>;
    }

    /** Loads completed items into the completed array */
    loadCompleted() {
        completed = [];
        for (let i in this.state.boardData.boards) {
            for (let j in this.state.boardData.boards[i].items) {

                var itemName = this.state.boardData.boards[i].items[j].name;

                for (let k in this.state.boardData.boards[i].items[j].column_values) {
                    if (this.state.boardData.boards[i].items[j].column_values[k].text == completed_state) {

                        completed[completed.length] = itemName;
                    }
                }   
            }
        }
        this.createBoxes();
    }

    /** Creates crates based on data in the completed array */
    createBoxes() {
        for (let i in completed) {

            let x = getRandomNumber(200, 400);
            let y = getRandomNumber(50, 400);
            let size = getRandomNumber(50, 80);

            let crate = Bodies.rectangle(x, y, size, size, {
                id: completed[i]
            });

            World.add(engine.world, [crate]);
            crates[crates.length = crate];
        }
    }
}

function getRandomNumber(min, max) {
    return Math.random() * (max - min) + min;
}

export default App;
