import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";

const monday = mondaySdk();
var completed = 0;

class App extends React.Component {
    constructor(props) {
        super(props);

        // Default state
        this.state = {
            settings: {},
            name: "",
        };
    }

    componentDidMount() {
        monday.listen(
            "events",
            (res) => {
                completed++;
                monday.execute("notice", {
                    message: "Completed: " + completed,
                    type: "success", // or "error" (red), or "info" (blue)
                    timeout: 10000,
                });
            },
            { type: "change_column_value" }
        );
    }

    render() {
        return <div className="App">Hello, mknknnkonday Apps!</div>;
    }
}

export default App;
