import React, { createElement } from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import Matter from "matter-js";

const monday = mondaySdk();
var completedIDs = [];

// module aliases
var Engine = Matter.Engine,
Render = Matter.Render,
World = Matter.World,
Bodies = Matter.Bodies,
Events = Matter.Events,
MouseConstraint = Matter.MouseConstraint,
Mouse = Matter.Mouse;

var engine,
render;

var crates = [];

class App extends React.Component {
    constructor(props) {
        super(props);

        // Default state
        this.state = {
            settings: {
                completed_state: "Done",
                completed_amount: 0
            },
            name: "",
        };
    }

    componentDidMount() {

        this.setupMatterJS();

        //Load boards listener
         monday.listen("context", this.getContext)

        // Settings changed listener
        monday.listen("settings", this.getSettings);
    }

    getContext = (res) => {
        this.setState({context: res.data});
        this.loadCompletedItems();
    }

    getSettings = (res) => {
        this.setState({ settings: res.data });
        this.loadCompletedItems();
    }

    loadCompletedItems() {
        completedIDs = [];
        for (let i in this.state.context.boardIds) {
            alert(this.state.context.boardIds[i]);
            monday.api(`query ($completedState: String!, $boardID: Int!) { items_by_column_values (board_id: $boardID, column_id: "status", column_value: $completedState) { name id } }`, {
                    variables: {completedState: this.state.settings.completed_state, boardID: "857811628"}
                })
                .then(res => {
                    crates.forEach((crate) => World.remove(engine.world, crate));
                    crates = [];
                    for (let i in res.data.items_by_column_values) {
                        this.createBox(res.data.items_by_column_values[i].name);
                        completedIDs[completedIDs.length] = res.data.items_by_column_values[i].id;
                    }
                    this.setState({completed_amount: completedIDs.length})
            });
        }
    }

    render() {
        return <div className="App" ref="launch_site">
            <p id="hover_label">Hover</p>
            <div className="launchDiv">
                <p className="launchTitle" id="title">{this.state.completed_amount} items ready to launch!</p>
                <button onClick={launch} className="launchButton">Launch now</button>
            </div>
        </div>;
    }

    /** Creates a new box/crate */
    createBox(name) {
        let x = getRandomInt(200, 800);
        let y = getRandomInt(50, 400);
        let size = getRandomInt(50, 70);

        let spriteNumber = getRandomInt(1, 8);

        let crate = Bodies.rectangle(x, y, size, size, {
            render: {
                sprite: {
                    texture: "/sprites/crates/crate_" + spriteNumber + ".png",
                    xScale: size / 475,
                    yScale: size / 475 } },
            id: name
        });

        World.add(engine.world, [crate]);
        crates[crates.length] = crate;
    }

    setupMatterJS() {
        engine = Engine.create();

        // create a renderer
        render = Render.create({
            element: this.refs.launch_site,
            engine: engine,
            options: {
                wireframes: false,
                background: '#fff',
                width: window.innerWidth,
                height: window.innerHeight - 100
            }
        });
        render.options.wireframes = false;

        var ground = Bodies.rectangle(window.innerWidth / 2 - 250, window.innerHeight - 100, window.innerWidth + 500, 20, { 
            isStatic: true,
            render: { fillStyle: '#fff' }
        });

        var mouse = Mouse.create(render.canvas),
        mouseConstraint = MouseConstraint.create(engine, {
            mouse: mouse,
            constraint: {
                stiffness: 0.2,
                render: { visible: false }
            }
        });

        World.add(engine.world, [ground, mouseConstraint]);
        Engine.run(engine);
        Render.run(render);

        Events.on(engine, 'tick', () => {
            var mousePosition = mouse.position;
            var cratesUnderMouse = Matter.Query.point(crates, {x: mousePosition.x, y: mousePosition.y});

            const label = document.getElementById("hover_label");
            if (cratesUnderMouse.length > 0) {
                let crate = cratesUnderMouse[0];
                label.innerHTML = crate.id;
                let labelHeight = (label.clientHeight + 1);
                let labelWidth = (label.clientWidth + 1);
                label.style.top = crate.position.y - 2 - labelHeight + "px";
                label.style.left = crate.position.x - labelWidth / 2 + "px";
                label.style.opacity = 1;
            } else {
                label.style.opacity = 0;
            }
        });
    }
}

function launch() {
    monday.execute("confirm", {
        message: "Are you sure? This will launch all completed items off the earth's atmosphere and they will be gone forever!", 
        confirmButton: "Launch!", 
        cancelButton: "Cancel", 
        excludeCancelButton: false
     }).then(() => {
         deleteCompleted();
     });
}

function deleteCompleted() {
    for (let i in completedIDs) {
        let itemID = parseInt(completedIDs[i]);
        monday.api(`mutation ($itemID: Int) { delete_item (item_id: $itemID) { id } }`, {
            variables: {itemID: itemID } }
        );
    }
}

function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

export default App;
